import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';
import RenterDashboard from './components/renter/Dashboard';
import PropertyDetail from './components/properties/PropertyDetail';

// Protected Route component
const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const { isAuthenticated, loading, user } = useAuth();
  
  if (loading) {
    return <div>Loading...</div>;
  }
  
  // Check if user is authenticated
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }
  
  // Check if role is allowed (if roles are specified)
  if (allowedRoles.length > 0 && !allowedRoles.includes(user?.role)) {
    return <Navigate to="/dashboard" />;
  }
  
  return children;
};

// Dashboard component to redirect based on role
const DashboardRouter = () => {
  const { user } = useAuth();
  
  if (user?.role === 'renter') {
    return <Navigate to="/renter/dashboard" />;
  } else if (user?.role === 'owner') {
    return <Navigate to="/owner/dashboard" />;
  } else if (user?.role === 'admin') {
    return <Navigate to="/admin/dashboard" />;
  }
  
  // Default fallback
  return <div>Loading dashboard...</div>;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Routes>
            {/* Public routes */}
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            
            {/* Dashboard router */}
            <Route path="/dashboard" element={
              <ProtectedRoute>
                <DashboardRouter />
              </ProtectedRoute>
            } />
            
            {/* Renter routes */}
            <Route path="/renter/dashboard" element={
              <ProtectedRoute allowedRoles={['renter']}>
                <RenterDashboard />
              </ProtectedRoute>
            } />
            
            {/* Property routes */}
            <Route path="/properties/:propertyId" element={
              <ProtectedRoute>
                <PropertyDetail />
              </ProtectedRoute>
            } />
            
            {/* Catch all */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
